    {
        snprintf(gsPCI9056_LastErr, sizeof(gsPCI9056_LastErr) - 1, "%s: NULL device %s\n",
            sFunc, !pDev ? "handle" : "context");
        ErrLog(gsPCI9056_LastErr);
        return FALSE;
    }

