static void ErrLog(const CHAR *sFormat, ...)
{
    va_list argp;
    va_start(argp, sFormat);
    vsnprintf(gsPCI9056_LastErr, sizeof(gsPCI9056_LastErr) - 1, sFormat, argp);
#if defined(DEBUG)
    WDC_Err("PCI9056 lib: %s", PCI9056_GetLastErr());
#endif
    va_end(argp);
}

static void TraceLog(const CHAR *sFormat, ...)
{
#if defined(DEBUG)
    CHAR sMsg[256];
    va_list argp;
    va_start(argp, sFormat);
    vsnprintf(sMsg, sizeof(sMsg) - 1, sFormat, argp);
    WDC_Trace("PCI9056 lib: %s", sMsg);
    va_end(argp);
#endif
}

